INSERT INTO `biolinks_themes` (`name`, `settings`, `is_enabled`, `order`, `last_datetime`, `datetime`) VALUES
('Zermatt', '{\"additional\":{\"custom_css\":\"#snowfall-element {\\r\\n\\tposition: fixed;\\r\\n\\ttop: 0;\\r\\n\\tleft: 0;\\r\\n\\twidth: 100vw;\\r\\n\\theight: 100vh;\\r\\n\\tpointer-events: none;\\r\\n\\tz-index: 0; \\/* Not too negative this time *\\/\\r\\n\\tbackground: transparent;\\r\\n}\",\"custom_js\":\"<script>\\r\\nconst snowfall = {};\\r\\n\\r\\n\\/\\/ Automatically create and insert the canvas element just after <body>\\r\\nsnowfall.canvas = document.createElement(\\\"canvas\\\");\\r\\nsnowfall.canvas.id = \\\"snowfall-element\\\";\\r\\ndocument.documentElement.appendChild(snowfall.canvas); \\/\\/ Append to <html>\\r\\n\\r\\n\\/\\/ Continue as before\\r\\nsnowfall.context = snowfall.canvas.getContext(\\\"2d\\\");\\r\\n\\r\\n\\/\\/ Snowflake constructor\\r\\nsnowfall.snowflake = function () {\\r\\n\\tthis.size = Math.random() * 2 + 2;\\r\\n\\tthis.x = (Math.random() * snowfall.canvas.width - this.size - 1) + this.size + 1;\\r\\n\\tthis.baseX = this.x;\\r\\n\\tthis.distance = Math.random() * 50 + 1;\\r\\n\\tthis.opacity = Math.random();\\r\\n\\tthis.radians = Math.random() * Math.PI * 2;\\r\\n\\tthis.fall_speed = Math.random() * 1.5 + 0.5;\\r\\n\\tthis.y = (Math.random() * snowfall.canvas.height - this.size - 1) + this.size + 1;\\r\\n\\tthis.draw = () => {\\r\\n\\t\\tif(this.y > snowfall.canvas.height + this.size) {\\r\\n\\t\\t\\tthis.y = -this.size;\\r\\n\\t\\t} else {\\r\\n\\t\\t\\tthis.y += this.fall_speed;\\r\\n\\t\\t}\\r\\n\\t\\tthis.radians += 0.02;\\r\\n\\t\\tthis.x = this.baseX + this.distance * Math.sin(this.radians);\\r\\n\\t\\tsnowfall.context.fillStyle = `rgba(255,255,255,${this.opacity})`;\\r\\n\\t\\tsnowfall.context.fillRect(this.x, this.y, this.size, this.size);\\r\\n\\t};\\r\\n};\\r\\n\\r\\n\\/\\/ Initial setup function\\r\\nsnowfall.setup = () => {\\r\\n\\tsnowfall.canvas.width = snowfall.context.canvas.clientWidth;\\r\\n\\tsnowfall.canvas.height = snowfall.context.canvas.clientHeight;\\r\\n\\tsnowfall.flakes = [];\\r\\n\\tconst flake_count = Math.ceil((snowfall.canvas.width * snowfall.canvas.height) \\/ 12000);\\r\\n\\tfor (let i = 0; i < flake_count; i++) {\\r\\n\\t\\tsnowfall.flakes.push(new snowfall.snowflake());\\r\\n\\t}\\r\\n};\\r\\n\\r\\nwindow.addEventListener(\\\"resize\\\", snowfall.setup);\\r\\n\\r\\n\\/\\/ Animation loop function\\r\\nsnowfall.animate = () => {\\r\\n\\trequestAnimationFrame(snowfall.animate);\\r\\n\\tsnowfall.context.clearRect(0, 0, snowfall.canvas.width, snowfall.canvas.height);\\r\\n\\tfor (let snowflake of snowfall.flakes) {\\r\\n\\t\\tsnowflake.draw();\\r\\n\\t}\\r\\n};\\r\\n\\r\\n\\/\\/ Let it snow!\\r\\nsnowfall.setup();\\r\\nsnowfall.animate();<\\/script>\"},\"biolink\":{\"background_type\":\"image\",\"background\":\"78564ffadd816470639d7f68149ee338.webp\",\"background_color_one\":null,\"background_color_two\":null,\"font\":\"karla\",\"font_size\":\"16\",\"background_blur\":2,\"background_brightness\":100,\"width\":8,\"block_spacing\":2,\"hover_animation\":\"smooth\"},\"biolink_block\":{\"text_color\":\"#ffffff\",\"description_color\":\"#FFF0F0\",\"background_color\":\"#02343B\",\"border_width\":\"1\",\"border_color\":\"#FFFFFF4A\",\"border_radius\":\"rounded\",\"border_style\":\"solid\",\"border_shadow_offset_x\":\"0\",\"border_shadow_offset_y\":\"0\",\"border_shadow_blur\":\"20\",\"border_shadow_spread\":\"0\",\"border_shadow_color\":\"#00000010\"},\"biolink_block_socials\":{\"color\":\"#FFFFFFBA\",\"background_color\":\"#228694A1\",\"border_radius\":\"rounded\"},\"biolink_block_paragraph\":{\"text_color\":\"#FFFFFFD9\"},\"biolink_block_heading\":{\"text_color\":\"#FFFFFF\"}}', 1, 6, '2025-03-25 01:31:19', '2025-03-23 04:31:05'),
('Seattle', '{\"additional\":{\"custom_css\":\"\",\"custom_js\":\"<script>\\r\\n    const computed_styles = getComputedStyle(document.body);\\r\\n\\r\\n    const original_background_image = computed_styles.backgroundImage;\\r\\n    const original_background_size = computed_styles.backgroundSize;\\r\\n    const original_background_position = computed_styles.backgroundPosition;\\r\\n    const original_background_repeat = computed_styles.backgroundRepeat;\\r\\n\\r\\n    const rain_images = [\\r\\n        `url(\'${site_url}themes\\/altum\\/assets\\/images\\/biolinks\\/rain.svg\')`,\\r\\n        `url(\'${site_url}themes\\/altum\\/assets\\/images\\/biolinks\\/rain.svg\')`,\\r\\n        `url(\'${site_url}themes\\/altum\\/assets\\/images\\/biolinks\\/rain.svg\')`\\r\\n    ];\\r\\n\\r\\n    const rain_sizes = [\'60%\', \'45%\', \'30%\'];\\r\\n    const rain_positions = [\'left top\', \'center top\', \'center top\'];\\r\\n    const rain_repeats = [\'repeat\', \'repeat\', \'repeat\'];\\r\\n\\r\\n    const all_images = rain_images.concat(original_background_image);\\r\\n    const all_sizes = rain_sizes.concat(original_background_size);\\r\\n    const all_positions = rain_positions.concat(original_background_position);\\r\\n    const all_repeats = rain_repeats.concat(original_background_repeat);\\r\\n\\r\\n    document.body.style.setProperty(\'background-image\', all_images.join(\', \'));\\r\\n    document.body.style.setProperty(\'background-size\', all_sizes.join(\', \'), \'important\');\\r\\n    document.body.style.setProperty(\'background-position\', all_positions.join(\', \'), \'important\');\\r\\n    document.body.style.setProperty(\'background-repeat\', all_repeats.join(\', \'), \'important\');\\r\\n<\\/script>\"},\"biolink\":{\"background_type\":\"image\",\"background\":\"25b10743f5d934e70250ffd557cee0a6.webp\",\"background_color_one\":null,\"background_color_two\":null,\"font\":\"inconsolata\",\"font_size\":\"16\",\"background_blur\":2,\"background_brightness\":100,\"width\":8,\"block_spacing\":2,\"hover_animation\":\"smooth\"},\"biolink_block\":{\"text_color\":\"#ffffff\",\"description_color\":\"#ffffff\",\"background_color\":\"#000000B5\",\"border_width\":\"0\",\"border_color\":\"\",\"border_radius\":\"straight\",\"border_style\":\"solid\",\"border_shadow_offset_x\":\"0\",\"border_shadow_offset_y\":\"0\",\"border_shadow_blur\":\"20\",\"border_shadow_spread\":\"0\",\"border_shadow_color\":\"#00000010\"},\"biolink_block_socials\":{\"color\":\"#ffffff\",\"background_color\":\"#00000000\",\"border_radius\":\"rounded\"},\"biolink_block_paragraph\":{\"text_color\":\"#5DC5D5\"},\"biolink_block_heading\":{\"text_color\":\"#FFFFFF\"}}', 1, 7, '2025-03-25 06:24:02', '2025-03-25 04:39:59'),
('Kyoto', '{\"additional\":{\"custom_css\":\"\",\"custom_js\":\"<script>\\r\\n\\r\\ndocument.body.style.backgroundImage = `url(\'${site_url}themes\\/altum\\/assets\\/images\\/biolinks\\/autumn_leaves.svg\'), ${document.body.style.backgroundImage}`;\\r\\n\\r\\n<\\/script>\"},\"biolink\":{\"background_type\":\"image\",\"background\":\"0fc5e5a6b52b9d58ffc6ecfb112c76df.webp\",\"background_color_one\":null,\"background_color_two\":null,\"font\":\"default\",\"font_size\":\"16\",\"background_blur\":3,\"background_brightness\":100,\"width\":8,\"block_spacing\":2,\"hover_animation\":\"smooth\"},\"biolink_block\":{\"text_color\":\"#ffffff\",\"description_color\":\"#ffffff\",\"background_color\":\"#BC5101ED\",\"border_width\":\"1\",\"border_color\":\"\",\"border_radius\":\"rounded\",\"border_style\":\"solid\",\"border_shadow_offset_x\":\"0\",\"border_shadow_offset_y\":\"0\",\"border_shadow_blur\":\"20\",\"border_shadow_spread\":\"0\",\"border_shadow_color\":\"#00000010\"},\"biolink_block_socials\":{\"color\":\"#FFE8C0\",\"background_color\":\"#0000007D\",\"border_radius\":\"round\"},\"biolink_block_paragraph\":{\"text_color\":\"#FFFFFF\"},\"biolink_block_heading\":{\"text_color\":\"#FFFFFF\"}}', 1, 8, '2025-03-26 02:46:32', '2025-03-26 02:20:40');

